#include&lt;iostream&gt;
using namespace std;
class B {
public:
    B(const string& ss) { cout &lt;&lt; "B constructor\n"; f(ss); }
    virtual void f(const string&) { cout &lt;&lt; "B::f\n";}
};
class D : public B {
public:
    D(const string & ss) :B(ss) { cout &lt;&lt; "D constructor\n";}
    void f(const string& ss) { cout &lt;&lt; "D::f\n"; s = ss; }
private:
    string s;
};
int main()
{
    D d("Hello");
}
