    using flow = std::tuple&lt;year_month_day, double, double, double&gt;;
    std::vector&lt;flow&gt; flows;
};

struct AssetSwap{
    Leg bondLeg;
    Leg fundingLeg;

    Leg getBondLeg() { return bondLeg; }
    Leg getFundingLeg() { return fundingLeg; }
};

